import React from 'react';
import { connect } from 'react-redux';

const CoffeeDetails = ({ coffee }) => {
	if (coffee === null) {
		return <div></div>;
	}
	return (
		<div>
			<h1>The Deets</h1>
			<h2>{coffee.name}</h2>
			<h3>${coffee.price}</h3>
		</div>
	);
};

const mapStateToProps = ({ selectedCoffee }) => {
	return {
		coffee: selectedCoffee,
	};
};

export default connect(mapStateToProps)(CoffeeDetails);
