import { ADD_TO_CART } from './types';
import coffees from '../apis/coffees';

export const addToCart = id => async dispatch => {
	const response = await coffees.get('/cart');
	const responseData = response.data;

	let selectedCoffee = await coffees.get(`/coffees?id=${action.payload.id}`);
	let coffeeToAdd = selectedCoffee.data[0];

	let cartArray = await coffees.get(`/cart?id=${action.payload.id}`);
	let itemInCart = cartArray.data[0];

	dispatch({
		type: ADD_TO_CART,
		payload: { id, responseData, coffeeToAdd, itemInCart },
	});
};
