import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { getCoffees } from '../../actions';

import { Card, Col, Row, Button } from 'antd';
import { PlusOutlined, EditOutlined, DeleteOutlined } from '@ant-design/icons';
const { Meta } = Card;

class CoffeeList extends Component {
	componentDidMount() {
		this.props.getCoffees();
	}

	displayCoffees() {
		return this.props.coffee.map(coffee => {
			return (
				<Col
					key={coffee.id}
					span={12}
					style={{ textAlign: 'center', border: '1px solid blue' }}
				>
					<Card
						hoverable
						style={{ width: 240, display: 'inline-block' }}
						cover={
							<img
								style={{
									width: 240,
									height: 200,
								}}
								alt={`Pic of ${coffee.name} not available`}
								src={coffee.picture}
							/>
						}
					>
						<Meta
							title={coffee.name}
							description={`$${coffee.price} - ${coffee.description}`}
						/>
						{this.props.currentUser === coffee.userId ? (
							<div>
								<Link to={`/coffees/edit/${coffee.id}`}>
									<Button icon={<EditOutlined />}>
										Edit
									</Button>
								</Link>
								<Link to={`/coffees/delete/${coffee.id}`}>
									<Button danger icon={<DeleteOutlined />}>
										Delete
									</Button>
								</Link>
							</div>
						) : null}
					</Card>
				</Col>
			);
		});
	}

	renderCreateButton() {
		if (this.props.isSignedIn) {
			return (
				<Button shape='circle' icon={<PlusOutlined />} size='large' />
			);
		}
		return <div></div>;
	}

	render() {
		return (
			<div className='site-card-wrapper'>
				<div style={{ textAlign: 'center' }}>
					<div style={{ display: 'inline-block' }}>
						<Row gutter={[16, 16]}>{this.displayCoffees()}</Row>
					</div>
				</div>
				<Link to='/coffees/create' style={{ float: 'right' }}>
					{this.renderCreateButton()}
				</Link>
			</div>
		);
	}
}

const mapStateToProps = state => {
	return {
		isSignedIn: state.auth.isSignedIn,
		currentUser: state.auth.userId,
		coffee: Object.values(state.coffee),
	};
};

export default connect(mapStateToProps, { getCoffees })(CoffeeList);
