import React, { Component } from 'react';
import { Field, reduxForm } from 'redux-form';
// import CoffeeForm from './CoffeeForm';

class CreateCoffee extends Component {
	renderTextInput(formProps, type) {
		return (
			<input
				type={type}
				onChange={formProps.input.onChange}
				value={formProps.input.value}
			/>
		);
	}
	render() {
		return (
			<form>
				<Field name='name' component={this.renderTextInput('text')} />
				<Field
					name='price'
					component={this.renderTextInput('number')}
				/>
				<Field
					name='description'
					component={this.renderTextInput('text')}
				/>
			</form>
		);
	}
}

export default reduxForm({ form: 'coffeeCreate' })(CreateCoffee);
