import coffees from '../apis/coffees';
import {
	SIGN_IN,
	SIGN_OUT,
	CREATE_COFFEE,
	GET_COFFEES,
	GET_COFFEE,
	EDIT_COFFEE,
	DELETE_COFFEE,
} from './types';

export const signIn = userId => {
	return {
		type: SIGN_IN,
		payload: userId,
	};
};

export const signOut = () => {
	return {
		type: SIGN_OUT,
	};
};

export const createCoffee = formValues => async dispatch => {
	const response = await coffees.post('/coffees', formValues);

	dispatch({ type: CREATE_COFFEE, payload: response.data });
};

export const getCoffees = () => async dispatch => {
	const response = await coffees.get('/coffees');

	dispatch({ type: GET_COFFEES });
};

export const getcoffee = () => async dispatch => {
	const response = await coffees.get('/coffees/:id');

	dispatch({ type: GET_COFFEE });
};

export const editCoffee = formValues => async dispatch => {
	const response = await coffees.put('/coffees/:id');

	dispatch({ type: EDIT_COFFEE, payload: formValues });
};

export const deleteCoffee = () => async dispatch => {
	const response = await coffees.delete('/coffees/:id');

	dispatch({ type: DELETE_COFFEE });
};
