import { ADD_TO_CART } from './types';
import coffees from '../apis/coffees';

export const addToCart = id => async dispatch => {
	const responseCart = await coffees.get('/cart');
	const cart = responseCart.data;
	console.log(cart);

	let responseCoffees = await coffees.get(`/coffees`);
	let allCoffees = responseCoffees.data;
	console.log(allCoffees);

	// need to figure out a way to filter for correct item, should return null if nothing...
	// possibly figure out how to add into cart with [id]: object data first
	// then we can filter? -> should still return null though...
	let itemExist = cart.items.find(item => item.id === id);

	dispatch({
		type: ADD_TO_CART,
		payload: { id, cart, itemExist },
		// payload: { id, cart, selectedCoffee, itemExist },
	});
};
