import React, { Component } from 'react';

class GoogleAuth extends Component {
	// when app first loads we dont know if signed in or not
	state = { isSignedIn: null };

	componentDidMount() {
		window.gapi.load('client:auth2', () => {
			window.gapi.client
				.init({
					clientId:
						'165355918127-02lo7eis00oftbrtvmbnasoccc87gbfs.apps.googleusercontent.com',
					scope: 'email',
				})
				.then(() => {
					// reference to get status of user, sign in/out
					this.auth = window.gapi.auth2.getAuthInstance();
					// get current auth status
					this.onAuthChange();
					// update the auth status whenever a change is made
					this.auth.isSignedIn.listen(this.onAuthChange);
				});
		});
	}

	onAuthChange = () => {
		this.setState({ isSignedIn: this.auth.isSignedIn.get() });
	};

	renderAuthButton() {
		if (this.state.isSignedIn === null) {
			return <div>Not sure if signed in</div>;
		} else if (this.state.isSignedIn) {
			return <div>I am signed in</div>;
		} else {
			return <div>Not signed in</div>;
		}
	}

	render() {
		return <div>{this.renderAuthButton()}</div>;
	}
}

export default GoogleAuth;
