import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { Modal, Button } from 'antd';
import { connect } from 'react-redux';
import history from '../history';
import { getCoffee, deleteCoffee } from '../actions';

class TheModal extends Component {
	componentDidMount() {
		this.props.getCoffee(this.props.match.params.id);
		console.log(this.props);
	}

	handleOk = () => {
		this.props.deleteCoffee(this.props.match.params.id);
	};

	handleCancel = () => {
		history.push('/coffees');
	};
	render() {
		return ReactDOM.createPortal(
			<div>
				<Modal
					title={`Are you sure want to delete ${this.props.coffee.name}`}
					visible={true}
					okText='Delete'
					onOk={this.handleOk}
					onCancel={this.handleCancel}
				>
					<p>Some contents...</p>
					<p>Some contents...</p>
					<p>Some contents...</p>
				</Modal>
			</div>,
			document.querySelector('#modal')
		);
	}
}

const mapStateToProps = (state, ownProps) => {
	console.log(ownProps);
	return { coffee: state.coffee[ownProps.match.params.id] };
};

export default connect(mapStateToProps, { getCoffee, deleteCoffee })(TheModal);
