import React, { Component } from 'react';
import { Field, reduxForm } from 'redux-form';

// import CoffeeForm from './CoffeeForm';
import { Form, Input, Button } from 'antd';

class CreateCoffee extends Component {
	renderTextInput({ input, label, type }) {
		// copying all properties into out input tag
		return (
			<div>
				<label>{label}</label>
				<input {...input} type={type} />
			</div>
		);
	}
	render() {
		return (
			<Form>
				<Form.Item label='Name of coffee'>
					<Field
						name='name'
						component={this.renderTextInput}
						type='text'
						label='Name of coffee'
					/>
				</Form.Item>
				<Field
					name='price'
					component={this.renderTextInput}
					type='number'
					label='Price'
				/>
				<Field
					name='description'
					component={this.renderTextInput}
					type='text'
					label='Description'
				/>
			</Form>
		);
	}
}

export default reduxForm({ form: 'coffeeCreate' })(CreateCoffee);
