import React, { Component } from 'react';
import CoffeeForm from './CoffeeForm';
import { editCoffee } from '../../actions';
import { connect } from 'react-redux';

class CoffeeEdit extends Component {
	componentDidMount() {
		console.log(this.props);
	}

	onSubmit = formVals => {
		this.props.editCoffee(this.props.match.params.id, formVals);
	};

	render() {
		return (
			<div>
				Coffee Edit Component
				<CoffeeForm onSubmit={this.onSubmit} initialValues />
			</div>
		);
	}
}

const mapStateToProps = (state, ownProps) => {
	console.log(ownProps);
	return { state: null };
};

export default connect(mapStateToProps, { editCoffee })(CoffeeEdit);
