import coffees from '../apis/coffees';
import { ADD_TO_CART } from '../actions/types';
import _ from 'lodash';

export default async (state = {}, action) => {
	switch (action.type) {
		case ADD_TO_CART:
			let coffeeToAdd = _.cloneDeep(action.payload.selectedCoffee);

			// if item in cart, update quantity and total price
			if (
				action.payload.itemExist &&
				action.payload.itemExist.id === action.payload.id
			) {
				console.log('item in cart');
			}
			// otherwise, add new item to cart
			else {
				console.log('item not in cart');

				// create quantity property where quantity = 1
				coffeeToAdd.quantity = 1;
				console.log('coffeeToAdd: ', coffeeToAdd);

				// add to total price
				let newTotal = coffeeToAdd.price + action.payload.cart[0].total;
				console.log('newTotal: ', newTotal);

				// update db.json file
				const response = await coffees.post('/cart', {
					...state,
					// total: newTotal
					total: newTotal,
					// add new object coffeeToAdd
					[action.payload.id]: coffeeToAdd,
				});
				return {
					response,
				};
			}
		default:
			return state;
	}
};
