import React, { Component } from 'react';
import { Field, reduxForm } from 'redux-form';
// import CoffeeForm from './CoffeeForm';

class CreateCoffee extends Component {
	renderTextInput({ input, label, type, rows, cols }) {
		// copying all properties into out input tag
		return (
			<div>
				<label>{label}</label>
				<input {...input} type={type} rows={rows} cols={cols} />
			</div>
		);
	}
	render() {
		return (
			<form>
				<Field
					name='name'
					component={this.renderTextInput}
					type='text'
					label='Name of coffee'
				/>
				<Field
					name='price'
					component={this.renderTextInput}
					type='number'
					label='Price'
				/>
				<Field
					name='description'
					component={this.renderTextInput}
					type='textarea'
					rows='4'
					cols='50'
					label='Description'
				/>
			</form>
		);
	}
}

export default reduxForm({ form: 'coffeeCreate' })(CreateCoffee);
