import React, { Component } from 'react';
import { connect } from 'react-redux';
import { selectCoffee } from '../actions';

class CoffeeList extends Component {
	displayCoffees() {
		return this.props.coffees.map(coffee => {
			return (
				<div key={coffee.name}>
					{coffee.name}
					<button onClick={this.props.selectCoffee}>Select</button>
				</div>
			);
		});
	}

	render() {
		return <div>{this.displayCoffees()}</div>;
	}
}

const mapStateToProps = state => {
	return {
		coffees: state.coffees,
	};
};

export default connect(mapStateToProps, { selectCoffee })(CoffeeList);
