import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { Modal, Button } from 'antd';
import { connect } from 'react-redux';
import history from '../history';
import { getCoffee, deleteCoffee } from '../actions';
import { LoadingOutlined } from '@ant-design/icons';

class TheModal extends Component {
	componentDidMount() {
		// this.props.getCoffee(this.props.match.params.id);
		console.log(this.props);
	}

	handleOk = () => {
		this.props.deleteCoffee(this.props.match.params.id);
	};

	handleCancel = () => {
		history.push('/coffees');
	};
	render() {
		if (!this.props.coffee) {
			return (
				<div>
					<LoadingOutlined />
				</div>
			);
		}
		return ReactDOM.createPortal(
			<div>
				<Modal
					title={`Delete Coffee ${this.props.coffee.name}?`}
					visible={true}
					okText='Delete'
					onOk={this.handleOk}
					onCancel={this.handleCancel}
				>
					<img
						src={`${this.props.coffee.picture}`}
						alt={`${this.props.coffee.name}`}
						style={{ height: '150px', width: '200px' }}
					/>
					<p>Price: {`$${this.props.coffee.price}`}</p>
					<p>Description: {`${this.props.coffee.description}`}</p>
				</Modal>
			</div>,
			document.querySelector('#modal')
		);
	}
}

export default connect(null, { getCoffee, deleteCoffee })(TheModal);
